pragma solidity ^0.5.0;

contract PassportStub {
    mapping (address => uint256) private payCounts;

    event Pay(address indexed from, address indexed to, uint256 value);

    function payCountOf(address owner) external view returns (uint256) {
        require(owner != address(0));

        return payCounts[owner];
    }

    function pay(address to, uint256 value) public returns (uint256){
        require(msg.sender != address(0));

        payCounts[msg.sender]++;
        emit Pay(msg.sender, to, value);
        return payCounts[msg.sender];
    }
}
